import React, { useRef, useState } from "react"
import { useAuth } from "../../contexts/AuthContext"
import { Link, useHistory } from "react-router-dom"
import {
Form,
FormWrap,
FormLabel,
FormInput,
FormH1,
FormButton,
Container,
Text,
Icon,
Alert,
FormContent} from './SigninElements'

import {Button, ButtonRouter} from '../ButtonElement';

export default function Dashboard() {
  const [error, setError] = useState("")
  const { currentUser, logout } = useAuth()
  const history = useHistory()

  async function handleLogout() {
    setError("")

    try {
      await logout()
      history.push("/login")
    } catch {
      setError("Failed to log out")
    }
  }

    return (
        <>
        <Container>
            <FormWrap>
                <Icon to ="/">GuardMyPi</Icon>
                <FormContent>
                <Form>
                    <FormH1>Profile</FormH1>
                    {error && <Alert>{error}</Alert>}
                    <FormLabel htmlFor='for'> Email: {currentUser.email}</FormLabel>
                    <FormButton onClick={handleLogout}>Log Out</FormButton>
                </Form>
            </FormContent>
            </FormWrap>

        </Container> 
    </>
    )

}
