import React, { useRef, useState } from "react"
import { useAuth } from "../../contexts/AuthContext"
import { Link, useHistory } from "react-router-dom"

import {
Form,
FormWrap,
FormLabel,
FormInput,
FormH1,
FormButton,
Container,
Text,
Icon,
Alert,
FormContent} from './SigninElements'

import {Button, ButtonRouter} from '../ButtonElement';

export default function Dashboard() {
  const [error, setError] = useState("")
  const { currentUser, logout } = useAuth()
  const history = useHistory()

  async function handleLogout() {
    setError("")

    try {
      await logout()
      history.push("/signin")
    } catch {
      setError("Failed to log out")
    }
  }

    return (
        <>
        <Container>
            <FormWrap>
                <Icon to ="/">GuardMyPi</Icon>
                <FormContent>
                    <FormH1>Profile</FormH1>
                    {error && <Alert>{error}</Alert>}
                    <Text>Email: {currentUser.email}</Text>
                    <FormButton onClick={handleLogout}> <Link to ="/signin" style={{textDecoration: 'none', textDecorationColor: '#ffff'}}>Log Out</Link></FormButton>
            </FormContent>
            </FormWrap>

        </Container> 
    </>
    )

}
